#!/usr/bin/env python3
"""
Simple Web Dashboard
Real-time surveillance system monitoring
"""

import json
import time
from datetime import datetime
from typing import Dict, Any

import redis
import requests
from fastapi import FastAPI, Request
from fastapi.responses import HTMLResponse
from fastapi.staticfiles import StaticFiles
from fastapi.templating import Jinja2Templates

app = FastAPI(title="Surveillance Dashboard", version="1.0.0")

# Static files and templates
app.mount("/static", StaticFiles(directory="static"), name="static")
templates = Jinja2Templates(directory="templates")

# Redis connection
redis_client = redis.Redis.from_url("redis://redis:6379", decode_responses=True)

# Service URLs
SERVICES = {
    "clip": "http://clip-encoder:8001",
    "rules": "http://rules-engine:8002", 
    "sam": "http://llm:8100",
    "qdrant": "http://vector-db:6333"
}

@app.get("/", response_class=HTMLResponse)
async def dashboard(request: Request):
    """Main dashboard page"""
    return templates.TemplateResponse("dashboard.html", {"request": request})

@app.get("/health")
async def health_check():
    """Dashboard health check"""
    return {
        "status": "healthy",
        "timestamp": time.time(),
        "services": len(SERVICES)
    }

@app.get("/api/status")
async def get_system_status():
    """Get overall system status"""
    status = {
        "timestamp": datetime.now().isoformat(),
        "services": {},
        "redis": {"status": "unknown"},
        "processing": {"frames_processed": 0, "rules_triggered": 0},
        "storage": {"disk_usage": "unknown"}
    }

    # Check Redis
    try:
        redis_client.ping()
        status["redis"]["status"] = "healthy"
        status["redis"]["connection"] = "ok"
    except Exception as e:
        status["redis"]["status"] = "unhealthy"
        status["redis"]["error"] = str(e)

    # Check each service
    for service_name, service_url in SERVICES.items():
        try:
            response = requests.get(f"{service_url}/health", timeout=5)
            if response.status_code == 200:
                status["services"][service_name] = {
                    "status": "healthy",
                    "response_time": response.elapsed.total_seconds(),
                    "data": response.json()
                }
            else:
                status["services"][service_name] = {
                    "status": "unhealthy", 
                    "response_code": response.status_code
                }
        except Exception as e:
            status["services"][service_name] = {
                "status": "unreachable",
                "error": str(e)
            }

    # Get processing stats from Redis
    try:
        stats = redis_client.lrange("processing_stats", 0, 0)
        if stats:
            latest_stats = json.loads(stats[0])
            status["processing"]["frames_processed"] = latest_stats.get("processed_images", 0)
            status["processing"]["rules_triggered"] = latest_stats.get("triggered_rules", 0)
    except Exception:
        pass

    return status

@app.get("/api/activity")
async def get_recent_activity(limit: int = 20):
    """Get recent activity log"""
    try:
        activities = redis_client.lrange("activity_log", 0, limit-1)
        return [json.loads(a) for a in activities]
    except Exception as e:
        return {"error": str(e), "activities": []}

@app.get("/api/notifications") 
async def get_notifications(limit: int = 10):
    """Get recent notifications"""
    try:
        notifications = redis_client.lrange("notifications", 0, limit-1)
        return [json.loads(n) for n in notifications]
    except Exception as e:
        return {"error": str(e), "notifications": []}

@app.get("/api/rules")
async def get_rules():
    """Get configured rules from rules engine"""
    try:
        response = requests.get(f"{SERVICES['rules']}/rules", timeout=5)
        if response.status_code == 200:
            return response.json()
        else:
            return {"error": f"Rules service returned {response.status_code}"}
    except Exception as e:
        return {"error": str(e), "rules": []}

if __name__ == "__main__":
    import uvicorn
    uvicorn.run(app, host="0.0.0.0", port=8080, log_level="info")

#!/usr/bin/env python3
"""
Claude Autonomous Shell (CASH) Server
MCP tool for autonomous command execution without human prompts
Usage: Load as MCP tool named "cash" for seamless shell access
"""

import asyncio
import json
import logging
import subprocess
import sys
from typing import Any, Dict, List, Optional
from pathlib import Path

# Configure logging
logging.basicConfig(level=logging.INFO)
logger = logging.getLogger(__name__)

class MCPShellServer:
    def __init__(self):
        self.allowed_commands = self._load_allowed_commands()
        self.command_log = []

    def _load_allowed_commands(self) -> set:
        """Load allowed commands from file"""
        allowed_file = Path("allowed_commands.txt")
        if allowed_file.exists():
            with open(allowed_file) as f:
                return set(line.strip() for line in f if line.strip() and not line.startswith('#'))
        return {
            'python3', 'pip', 'git', 'curl', 'ls', 'cat', 'head', 'tail',
            'mkdir', 'cp', 'mv', 'rm', 'find', 'grep', 'which', 'say',
            'docker', 'docker-compose', 'nvidia-smi'
        }

    def _is_command_allowed(self, command: str) -> bool:
        """Check if command is in allowlist"""
        base_cmd = command.split()[0] if command else ""
        return base_cmd in self.allowed_commands

    def _log_command(self, command: str, result: Dict[str, Any]):
        """Log command execution"""
        log_entry = {
            "timestamp": asyncio.get_event_loop().time(),
            "command": command,
            "success": result.get("success", False),
            "exit_code": result.get("exit_code", -1)
        }
        self.command_log.append(log_entry)
        # Keep last 100 commands
        if len(self.command_log) > 100:
            self.command_log = self.command_log[-100:]

    async def execute_command(self, command: str, timeout: int = 30) -> Dict[str, Any]:
        """Execute shell command with safety checks"""
        if not self._is_command_allowed(command):
            result = {
                "success": False,
                "error": f"Command not allowed: {command.split()[0]}",
                "stdout": "",
                "stderr": "",
                "exit_code": -1
            }
            self._log_command(command, result)
            return result

        try:
            # Execute command
            process = await asyncio.create_subprocess_shell(
                command,
                stdout=asyncio.subprocess.PIPE,
                stderr=asyncio.subprocess.PIPE,
                limit=1024*1024  # 1MB limit
            )

            try:
                stdout, stderr = await asyncio.wait_for(
                    process.communicate(), timeout=timeout
                )

                result = {
                    "success": process.returncode == 0,
                    "stdout": stdout.decode('utf-8', errors='replace'),
                    "stderr": stderr.decode('utf-8', errors='replace'),
                    "exit_code": process.returncode
                }

            except asyncio.TimeoutError:
                process.kill()
                await process.wait()
                result = {
                    "success": False,
                    "error": f"Command timed out after {timeout}s",
                    "stdout": "",
                    "stderr": "",
                    "exit_code": -1
                }

        except Exception as e:
            result = {
                "success": False,
                "error": str(e),
                "stdout": "",
                "stderr": "",
                "exit_code": -1
            }

        self._log_command(command, result)
        return result

    async def handle_mcp_request(self, request: Dict[str, Any]) -> Dict[str, Any]:
        """Handle MCP protocol request"""
        try:
            method = request.get("method")
            params = request.get("params", {})

            if method == "execute_command":
                command = params.get("command", "")
                timeout = params.get("timeout", 30)

                if not command:
                    return {
                        "error": {"code": -1, "message": "No command provided"}
                    }

                result = await self.execute_command(command, timeout)
                return {"result": result}

            elif method == "list_allowed_commands":
                return {"result": {"commands": sorted(list(self.allowed_commands))}}

            elif method == "get_command_log":
                limit = params.get("limit", 10)
                return {"result": {"log": self.command_log[-limit:]}}

            else:
                return {
                    "error": {"code": -1, "message": f"Unknown method: {method}"}
                }

        except Exception as e:
            return {
                "error": {"code": -1, "message": str(e)}
            }

async def main():
    """Main MCP server loop"""
    server = MCPShellServer()

    print("MCP Shell Server starting...", file=sys.stderr)
    print(f"Allowed commands: {len(server.allowed_commands)}", file=sys.stderr)

    # Read JSON-RPC messages from stdin
    while True:
        try:
            line = await asyncio.get_event_loop().run_in_executor(
                None, sys.stdin.readline
            )

            if not line:
                break

            request = json.loads(line.strip())
            response = await server.handle_mcp_request(request)

            # Add request ID if present
            if "id" in request:
                response["id"] = request["id"]

            print(json.dumps(response))
            sys.stdout.flush()

        except json.JSONDecodeError:
            error_response = {
                "error": {"code": -32700, "message": "Parse error"}
            }
            print(json.dumps(error_response))
            sys.stdout.flush()

        except Exception as e:
            error_response = {
                "error": {"code": -1, "message": str(e)}
            }
            print(json.dumps(error_response))
            sys.stdout.flush()

if __name__ == "__main__":
    asyncio.run(main())

