"""
Test /segment_all endpoint
Try to get the SAM API working
"""

import json
import urllib.request
import base64
from pathlib import Path

def test_segment_all():
    """Test the /segment_all endpoint which should work without points"""

    sam_url = "http://llm:8100"

    # Load test image
    test_image = Path("src/cropped_item_5.jpg")
    if not test_image.exists():
        print("‚ùå Test image not found")
        return

    image_data = test_image.read_bytes()
    print(f"üì∏ Image size: {len(image_data)} bytes")

    # Try different formats for /segment_all
    formats_to_try = [
        {
            "name": "Raw binary image",
            "data": image_data,
            "headers": {"Content-Type": "image/jpeg"}
        },
        {
            "name": "Base64 in JSON", 
            "data": json.dumps({"image": base64.b64encode(image_data).decode()}).encode(),
            "headers": {"Content-Type": "application/json"}
        },
        {
            "name": "Base64 without JSON wrapper",
            "data": base64.b64encode(image_data),
            "headers": {"Content-Type": "text/plain"}
        },
        {
            "name": "Image data field",
            "data": json.dumps({"data": base64.b64encode(image_data).decode()}).encode(),
            "headers": {"Content-Type": "application/json"}
        }
    ]

    for fmt in formats_to_try:
        print(f"\\nüß™ Testing: {fmt['name']}")
        try:
            req = urllib.request.Request(
                f"{sam_url}/segment_all",
                data=fmt["data"],
                headers=fmt["headers"]
            )

            with urllib.request.urlopen(req, timeout=30) as response:
                result = response.read().decode()
                print(f"‚úÖ SUCCESS! Response length: {len(result)}")

                # Try to parse as JSON
                try:
                    parsed = json.loads(result)
                    if "masks" in parsed:
                        print(f"üé≠ Found {len(parsed['masks'])} masks!")

                        # Save successful format and result
                        with open("working_sam_response.json", "w") as f:
                            json.dump({
                                "working_format": fmt,
                                "response": parsed
                            }, f, indent=2)

                        return True
                    else:
                        print(f"üìã Response keys: {list(parsed.keys())}")

                except json.JSONDecodeError:
                    print(f"üìÑ Non-JSON response: {result[:200]}...")

        except urllib.error.HTTPError as e:
            try:
                error_body = e.read().decode()
                print(f"‚ùå HTTP {e.code}: {error_body[:200]}")
            except:
                print(f"‚ùå HTTP {e.code}: {e.reason}")
        except Exception as e:
            print(f"‚ùå {type(e).__name__}: {e}")

    return False

if __name__ == "__main__":
    success = test_segment_all()
    if success:
        print("\\nüéâ Found working format!")
    else:
        print("\\nüòû No working format found")

