import { json } from '@sveltejs/kit';
import { getNoteById, getAttachmentsByNote } from '$lib/server/db';

export async function GET({ params }) {
  try {
    const noteId = params.id;
    const note = getNoteById(noteId);

    if (!note) {
      return json({ error: 'Note not found' }, { status: 404 });
    }

    // Get attachments for this note
    const attachments = getAttachmentsByNote(noteId);

    // Parse metadata if it exists
    try {
      if (note.metadata) {
        note.metadata = JSON.parse(note.metadata);
      }
    } catch (error) {
      console.error('Error parsing note metadata:', error);
      note.metadata = { error: 'Failed to parse metadata' };
    }

    return json({ note, attachments });
  } catch (error) {
    console.error(`Error fetching note ${params.id}:`, error);
    return json({ error: 'Failed to fetch note' }, { status: 500 });
  }
}

