import Database from 'better-sqlite3';
import { dev } from '$app/environment';
import fs from 'fs';
import path from 'path';

// In development, the DB path is relative to the project root
// In production, you might want to use an absolute path or environment variable
const DB_PATH = dev 
  ? '../../apple_all.db'  // Path relative to project root
  : process.env.DB_PATH || '/app/data/apple_all.db';

// Ensure the database exists
function ensureDbExists(dbPath) {
  if (!fs.existsSync(dbPath)) {
    console.error(`Database file not found at ${dbPath}`);
    console.error('Please run the import_all_objects.py script first to create the database');
    throw new Error(`Database file not found at ${dbPath}`);
  }
}

// Initialize database connection
function getDb() {
  const dbPath = path.resolve(DB_PATH);
  ensureDbExists(dbPath);

  try {
    return new Database(dbPath, { readonly: true });
  } catch (error) {
    console.error('Failed to connect to database:', error);
    throw error;
  }
}

// Get accounts
export function getAccounts() {
  const db = getDb();
  return db.prepare('SELECT * FROM accounts').all();
}

// Get folders
export function getFolders() {
  const db = getDb();
  return db.prepare('SELECT * FROM folders').all();
}

// Get notes by folder
export function getNotesByFolder(folderId) {
  const db = getDb();
  return db.prepare('SELECT * FROM notes WHERE folder_id = ? ORDER BY updated_at DESC').all(folderId);
}

// Get all notes
export function getAllNotes(limit = 100, offset = 0) {
  const db = getDb();
  return db.prepare('SELECT * FROM notes ORDER BY updated_at DESC LIMIT ? OFFSET ?').all(limit, offset);
}

// Get note by ID
export function getNoteById(id) {
  const db = getDb();
  return db.prepare('SELECT * FROM notes WHERE id = ?').get(id);
}

// Get attachments for a note
export function getAttachmentsByNote(noteId) {
  const db = getDb();
  return db.prepare('SELECT * FROM attachments WHERE note_id = ?').all(noteId);
}

// Get folder structure with counts
export function getFolderStructure() {
  const db = getDb();
  const query = `
    SELECT 
      f.id,
      f.name,
      f.parent_id,
      f.path,
      COUNT(n.id) as note_count
    FROM 
      folders f
    LEFT JOIN 
      notes n ON f.id = n.folder_id
    GROUP BY 
      f.id
    ORDER BY 
      f.name
  `;
  return db.prepare(query).all();
}

// Get stats about the database
export function getStats() {
  const db = getDb();

  const noteCount = db.prepare('SELECT COUNT(*) as count FROM notes').get().count;
  const folderCount = db.prepare('SELECT COUNT(*) as count FROM folders').get().count;
  const attachmentCount = db.prepare('SELECT COUNT(*) as count FROM attachments').get().count;
  const accountCount = db.prepare('SELECT COUNT(*) as count FROM accounts').get().count;

  const passwordProtected = db.prepare(
    "SELECT COUNT(*) as count FROM notes WHERE metadata LIKE '%\"is_password_protected\": true%'"
  ).get().count;

  const folderStats = db.prepare(`
    SELECT f.name, COUNT(n.id) as count 
    FROM folders f
    LEFT JOIN notes n ON f.id = n.folder_id
    GROUP BY f.id
    ORDER BY count DESC
  `).all();

  return {
    noteCount,
    folderCount,
    attachmentCount,
    accountCount,
    passwordProtected,
    folderStats
  };
}

// Search notes
export function searchNotes(query, limit = 100) {
  const db = getDb();
  const searchTerm = `%${query}%`;

  return db.prepare(`
    SELECT * FROM notes 
    WHERE title LIKE ? OR content LIKE ? 
    ORDER BY updated_at DESC
    LIMIT ?
  `).all(searchTerm, searchTerm, limit);
}

