 1	<script>
 2	  export let note;
 3	  export let attachments = [];
 4	  
 5	  // Format the date to a readable format
 6	  function formatDate(isoDate) {
 7	    if (!isoDate) return 'Unknown date';
 8	    
 9	    try {
10	      const date = new Date(isoDate);
11	      return new Intl.DateTimeFormat('en-US', { 
12	        year: 'numeric', 
13	        month: 'short', 
14	        day: 'numeric',
15	        hour: '2-digit',
16	        minute: '2-digit'
17	      }).format(date);
18	    } catch (e) {
19	      return 'Invalid date';
20	    }
21	  }
22	  
23	  // Format the metadata for display
24	  function formatMetadata(metadata) {
25	    if (!metadata) return 'No metadata';
26	    
27	    try {
28	      const parsed = typeof metadata === 'string' ? JSON.parse(metadata) : metadata;
29	      // Only show a few important fields
30	      const importantFields = ['icloud_pk', 'has_note_data', 'is_password_protected', 'ZTYPE', 'ZTYPEUTI'];
31	      const filtered = Object.fromEntries(
32	        Object.entries(parsed)
33	          .filter(([key]) => importantFields.includes(key))
34	      );
35	      return JSON.stringify(filtered, null, 2);
36	    } catch (e) {
37	      return 'Invalid metadata';
38	    }
39	  }
40	</script>
41	
