#!/usr/bin/env python3
"""
GitHub integration for agent framework.
"""
import os
from typing import Dict, List, Optional, Any
import logging
import github
from github import Github
from github.Issue import Issue
from github.Repository import Repository

logger = logging.getLogger('github')

class GithubIntegration:
    """Handles GitHub API interactions."""

    def __init__(
        self, 
        repo_owner: str, 
        repo_name: str, 
        token: str,
        issue_number: Optional[int] = None
    ):
        """Initialize GitHub integration."""
        self.repo_owner = repo_owner
        self.repo_name = repo_name
        self.token = token
        self.issue_number = issue_number
        self.repo = None
        self.issue = None

        self._initialize()

    def _initialize(self):
        """Initialize GitHub client and fetch repository/issue."""
        try:
            # Initialize GitHub client
            self.client = Github(self.token)

            # Get repository
            self.repo = self.client.get_repo(f"{self.repo_owner}/{self.repo_name}")

            # Get issue if specified
            if self.issue_number:
                self.issue = self.repo.get_issue(self.issue_number)

            logger.info(f"GitHub integration initialized for {self.repo_owner}/{self.repo_name}")

        except Exception as e:
            logger.error(f"Failed to initialize GitHub integration: {e}")
            raise

    def post_comment(self, message: str) -> bool:
        """Post a comment on the issue."""
        if not self.issue:
            logger.warning("No issue specified, cannot post comment")
            return False

        try:
            self.issue.create_comment(message)
            logger.info(f"Posted comment to issue #{self.issue_number}")
            return True
        except Exception as e:
            logger.error(f"Failed to post comment: {e}")
            return False

    def update_issue(self, title: Optional[str] = None, body: Optional[str] = None, 
                    state: Optional[str] = None, labels: Optional[List[str]] = None) -> bool:
        """Update issue properties."""
        if not self.issue:
            logger.warning("No issue specified, cannot update")
            return False

        try:
            # Update specified fields
            kwargs = {}
            if title:
                kwargs['title'] = title
            if body:
                kwargs['body'] = body
            if state and state in ('open', 'closed'):
                kwargs['state'] = state

            if kwargs:
                self.issue.edit(**kwargs)

            # Update labels if specified
            if labels:
                self.issue.set_labels(*labels)

            logger.info(f"Updated issue #{self.issue_number}")
            return True

        except Exception as e:
            logger.error(f"Failed to update issue: {e}")
            return False

    def check_issue_labels(self) -> List[str]:
        """Get the current labels on the issue."""
        if not self.issue:
            logger.warning("No issue specified, cannot check labels")
            return []

        try:
            return [label.name for label in self.issue.get_labels()]
        except Exception as e:
            logger.error(f"Failed to get issue labels: {e}")
            return []

    def add_label(self, label: str) -> bool:
        """Add a label to the issue."""
        if not self.issue:
            logger.warning("No issue specified, cannot add label")
            return False

        try:
            current_labels = self.check_issue_labels()
            if label not in current_labels:
                self.issue.add_to_labels(label)
                logger.info(f"Added label '{label}' to issue #{self.issue_number}")
            return True
        except Exception as e:
            logger.error(f"Failed to add label: {e}")
            return False

    def remove_label(self, label: str) -> bool:
        """Remove a label from the issue."""
        if not self.issue:
            logger.warning("No issue specified, cannot remove label")
            return False

        try:
            current_labels = self.check_issue_labels()
            if label in current_labels:
                self.issue.remove_from_labels(label)
                logger.info(f"Removed label '{label}' from issue #{self.issue_number}")
            return True
        except Exception as e:
            logger.error(f"Failed to remove label: {e}")
            return False

