#!/usr/bin/env python3
"""
Agent runner for executing smolagents tasks.
"""
import os
import sys
import time
import json
import yaml
import logging
from pathlib import Path
from typing import Dict, List, Optional, Any

# SmalAgents imports
from smolagents import OpenAIServerModel, CodeAgent

# Local imports
from github import GithubIntegration

# Configure logging
logging.basicConfig(
    level=logging.INFO,
    format='%(asctime)s - %(name)s - %(levelname)s - %(message)s',
    handlers=[logging.StreamHandler()]
)
logger = logging.getLogger('agent')

# Constants
CONFIG_PATH = Path('/app/config.yaml')
REPO_PATH = Path('/repo')
STATE_PATH = Path('/agent_state')
MAX_COMMENTS_PER_RUN = 3

class Agent:
    """Main agent class that orchestrates task execution."""

    def __init__(self, config_path: Path = CONFIG_PATH):
        """Initialize the agent with configuration."""
        self.config = self._load_config(config_path)
        self.comment_count = 0
        self.github = None
        self.model = None
        self.agent = None

        # Ensure state directory exists
        STATE_PATH.mkdir(exist_ok=True)

        # Setup integrations
        self._setup_model()
        if self.config.get('github', {}).get('enabled', False):
            self._setup_github()

    def _load_config(self, config_path: Path) -> Dict[str, Any]:
        """Load configuration from YAML file."""
        try:
            if config_path.exists():
                with open(config_path, 'r') as f:
                    return yaml.safe_load(f)
            else:
                logger.warning(f"Config file not found at {config_path}, using defaults")
                return {}
        except Exception as e:
            logger.error(f"Error loading config: {e}")
            return {}

    def _setup_model(self):
        """Initialize the LLM model connection."""
        model_config = self.config.get('model', {})
        api_base = model_config.get('api_base', 'http://localhost:5000/v1')
        model_id = model_config.get('model_id', 'default_model')

        logger.info(f"Connecting to model at {api_base}")
        self.model = OpenAIServerModel(
            model_id=model_id,
            api_base=api_base,
            api_key="not-needed"  # TabbyAPI typically doesn't need auth
        )

    def _setup_github(self):
        """Initialize GitHub integration if enabled."""
        github_config = self.config.get('github', {})
        self.github = GithubIntegration(
            repo_owner=github_config.get('owner'),
            repo_name=github_config.get('repo'),
            token=github_config.get('token'),
            issue_number=github_config.get('issue_number')
        )

    def post_comment(self, message: str) -> bool:
        """Post a comment to GitHub if integration is enabled."""
        if not self.github or self.comment_count >= MAX_COMMENTS_PER_RUN:
            logger.info(f"Comment count: {self.comment_count}/{MAX_COMMENTS_PER_RUN}")
            return False

        try:
            self.github.post_comment(message)
            self.comment_count += 1
            return True
        except Exception as e:
            logger.error(f"Error posting comment: {e}")
            return False

    def load_prompt(self, role: str) -> str:
        """Load prompt for the specified agent role."""
        prompt_path = Path(f'/app/prompts/{role}.md')

        if not prompt_path.exists():
            logger.warning(f"Prompt not found for role {role}, using default")
            return "You are a developer agent. Help implement the requested task."

        with open(prompt_path, 'r') as f:
            return f.read()

    def run_task(self, task: str, role: str = 'developer') -> Dict[str, Any]:
        """Execute a task using the agent."""
        if not self.model:
            logger.error("Model not initialized")
            return {"status": "error", "message": "Model not initialized"}

        # Load role-specific prompt
        system_prompt = self.load_prompt(role)

        # Create tools with ability to comment
        tools = [
            {
                "type": "function",
                "function": {
                    "name": "post_update",
                    "description": "Post a progress update or decision explanation",
                    "parameters": {
                        "type": "object",
                        "properties": {
                            "message": {
                                "type": "string", 
                                "description": "The message to post as a progress update"
                            }
                        },
                        "required": ["message"]
                    }
                }
            }
        ]

        # Initialize the agent
        self.agent = CodeAgent(
            model=self.model,
            tools=tools,
            add_base_tools=True,
            system_prompt=system_prompt
        )

        # Add post_update tool implementation
        def post_update(message: str) -> str:
            if self.post_comment(message):
                return "Update posted successfully"
            else:
                return "Update could not be posted (max comments reached or GitHub disabled)"

        self.agent.register_tool("post_update", post_update)

        try:
            # Run the agent
            logger.info(f"Running task: {task}")
            start_time = time.time()

            if self.github:
                self.post_comment(f"ðŸš€ Starting work on task as {role} agent\n\n```\n{task}\n```")

            result = self.agent.run(task)

            elapsed = time.time() - start_time
            logger.info(f"Task completed in {elapsed:.2f} seconds")

            # Post completion message
            if self.github:
                status = "âœ… Completed" if "error" not in result else "âŒ Encountered issues"
                self.post_comment(f"{status} after {elapsed:.2f} seconds\n\n```\n{result}\n```")

            return {
                "status": "success",
                "result": result,
                "elapsed_time": elapsed
            }

        except Exception as e:
            logger.error(f"Error running task: {e}")

            if self.github:
                self.post_comment(f"âŒ Error encountered: {str(e)}")

            return {
                "status": "error",
                "message": str(e)
            }

def main():
    """Main entry point for the agent."""
    # Parse arguments
    if len(sys.argv) < 2:
        print("Usage: agent.py <task> [role]")

