const modelProfilesDiv = Array.from(document.querySelectorAll('div')).find(div => 
  div.textContent.trim() === 'MODEL PROFILES'
);
if (modelProfilesDiv) {
  const button = modelProfilesDiv.parentElement.querySelector('button');
  if (button) {
    button.click();
    'Clicked MODEL PROFILES expand button';
  } else {
    'Button not found';
  }
} else {
  'MODEL PROFILES div not found';
}

// Find all buttons and look for the one near MODEL PROFILES
const buttons = Array.from(document.querySelectorAll('button'));
const modelProfilesButton = buttons.find(btn => {
  const parent = btn.parentElement;
  return parent && parent.textContent.includes('MODEL PROFILES');
});

if (modelProfilesButton) {
  modelProfilesButton.click();
  'Clicked expand button';
} else {
  // Log what we found
  const texts = buttons.slice(0, 10).map(b => b.textContent.trim() + ' | ' + (b.parentElement?.textContent.trim() || 'no parent'));
  texts;
}

