#!/usr/bin/env python3
"""
Terminal MCP Server - Provides terminal command execution via subprocess
Exposes commands to execute bash commands with timeout and output capture

IMPORTANT: Cross-platform terminal command execution using subprocess.
- Executes commands directly using subprocess with proper shell isolation
- Supports working directory changes and timeout handling
- Returns both stdout and stderr output
- More reliable than AppleScript-based solutions
"""

import asyncio
import json
import subprocess
import tempfile
import os
import platform
import sys
from pathlib import Path
from typing import Dict, Any, List

